// https://github.com/jsdom/jsdom
// A fake HTML Document run in the terminal (Node.js)
// A JavaScript implementation of the WHATWG DOM and HTML standards, for use with Node.js
import jsdom from 'jsdom';
import _$ from 'jquery';
import TestUtils from 'react-addons-test-utils';
import ReactDOM from 'react-dom';
import chai, { expect } from 'chai';
import React from 'react'; // needed whenever we use JSX
import { Provider } from 'react-redux';
import { createStore } from 'redux';
import reducers from '../src/reducers';
import chaiJquery from 'chai-jquery';

// Set up testing environment to run like a browser in the command line
// not window, but global variable

const { JSDOM } = jsdom;
const { document } = (new JSDOM('<!doctype html><html><body></body></html>')).window;
global.document = document;

global.window = global.document.defaultView;
// overwrite default $ of jquery / hook up our fake dom to jquery
const $ =  _$(global.window);

// build 'renderComponent' helper that should render a given react class
function renderComponent(ComponentClass, props, state){

    // https://reactjs.org/docs/test-utils.html
    const componentInstance = TestUtils.renderIntoDocument(
        <Provider store={createStore(reducers, state)}>
            <ComponentClass {...props} />
        </Provider>
    );

    return $(ReactDOM.findDOMNode(componentInstance)); // produces HTML and wraps it into jquery to be accessible


}

// build helper to for simulating events
// makes this possible for all jquery elements: $('div').simulate()
$.fn.simulate = function(eventName, value){

    if (value){
        this.val(value); // val() is a jquery function
    }

    // this is the reference to the dom element, and we need the first one of the array
    TestUtils.Simulate[eventName](this[0]);

};

// Set up chai-jquery
chaiJquery(chai, chai.util, $);

export { renderComponent, expect };

import { combineReducers } from 'redux';
import commentsReducer from './comments';

const rootReducer = combineReducers({
    // state: (state = {}) => state
    // for comment list to work: make an array out of it
    // dummy reducer:
    // comments: (state = []) => state
    comments: commentsReducer
});

export default rootReducer;

import { SAVE_COMMENT } from '../actions/types';

export default function(state = [], action){

    switch(action.type){
        case SAVE_COMMENT:
            // return state.concat([action.payload]);
            // better:
            return [ ...state, action.payload];
    }
    return state;
}

import { expect } from '../test_helper';

import { SAVE_COMMENT } from '../../src/actions/types';
import { saveComment } from '../../src/actions';

describe('actions', () => {
    describe('saveComment', () => {
        it('has the correct type', () => {

            // action creator returns an action
            const action = saveComment();
            expect(action.type).to.equal(SAVE_COMMENT);
        });

        it('has the correct payload', () => {

            const action = saveComment('new comment');
            expect(action.payload).to.equal('new comment');
        });

    });

});

import { SAVE_COMMENT } from './types';

// action creator:
export function saveComment(comment){
    return {
        type: SAVE_COMMENT,
        payload: comment
    }

}

import { renderComponent, expect } from '../test_helper';
import App from '../../src/components/app';

// Use 'describe' to group together similar tests
describe('App', () => {

    let component;

    // First create an instance of App
    beforeEach(() => {
        component = renderComponent(App);
    });

    // Use 'it' to test a single attribute of a target
    it('shows comment box', () => {

        // Use 'expect' to make an 'assertion' about a target
        expect(component.find('.comment-box')).to.exist;
    });

    it('shows comment list', () =>{
        expect(component.find('.comment-list')).to.exist;
    });
});

import React, {Component} from 'react';
import CommentBox from './comment_box';
import CommentList from './comment_list';

class App extends Component {
    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <img src="/logo.svg" className="App-logo" alt="logo"/>
                    <h1 className="App-title">Welcome to React</h1>
                </header>
                <div className="Test-container">
                    <CommentBox />
                    <CommentList />
                </div>
            </div>
        );
    }
}

export default App;

import React, { Component } from 'react';

// to turn a component into a container to have access to the state:
import { connect } from 'react-redux';
// imports ALL action creators and stores them in variable actions
import * as actions from '../actions';

// not exporting our component any more, but exporting a container instead:
// export default class CommentBox extends Component {
class CommentBox extends Component {
    constructor(props) {
        super(props);

        this.state = { comment: '' };
    }

    handleChange(event){
        this.setState({ comment: event.target.value });
    }

    handleSubmit(event){
        // keep the form from submitting to itself
        event.preventDefault();

        // new after having wired up the Container with the action creators
        this.props.saveComment(this.state.comment);
        this.setState({ comment: ''});
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit.bind(this)} className="comment-box">
                <h4>Add a comment</h4>
                <textarea value={this.state.comment} onChange={this.handleChange.bind(this)} />
                <div>
                    <button action="submit">Submit Comment</button>
                </div>
            </form>
        )
    }
}

// to turn a component into a container, but we need no state at all ! So no mapStateToProps here !
// instead of mapDispatchToProps we insert actions here (shortcut)
// this will bind all action creators to our CommentBox under this.props (e.g. this.props.saveComment)
export default connect(null, actions)(CommentBox);

import React from 'react';

// to turn a component into a container to have access to the state:
import { connect } from 'react-redux';

const CommentList = (props) => {
    const list = props.comments.map(comment => <li key={comment}>{comment}</li>);
    return (
        <ul className="comment-list">{list}</ul>
    );
};

function mapStateToProps(state){
    return { comments: state.comments};
}

// to turn a component into a container:
export default connect(mapStateToProps)(CommentList);

import { renderComponent, expect } from '../test_helper';
import CommentBox from '../../src/components/comment_box';

describe('CommentBox', () => {

    let component;

    beforeEach(() => {
        component = renderComponent(CommentBox);
    });

    it('has the correct class', () => {
        expect(component).to.have.class('comment-box');
    });

    it('has a text area', () => {
        expect(component.find('textarea')).to.exist;
    });

    it('has a button', () => {
        expect(component.find('button')).to.exist;
    });

    describe('entering some text', () => {

        beforeEach(() => {
            component.find('textarea').simulate('change', 'new comment');
        });

        it('shows that text in the textarea', () => {
            expect(component.find('textarea')).to.have.value('new comment');
        });

        it('when submitted, clears the input', () => {
            // console.log(component)'s output:
            //  <form class="comment-box" data-reactid=".1r1lut2gwsg">
            //      <textarea data-reactid=".1r1lut2gwsg.0"></textarea>
            //      <button data-reactid=".1r1lut2gwsg.1">Submit Comment</button>
            //  </form>
            component.simulate('submit');
            expect(component.find('textarea')).to.have.value('');
        });
    });
});

import { renderComponent, expect } from '../test_helper';
import CommentList from '../../src/components/comment_list';

describe('CommentList', () => {

    let component;

    beforeEach(() => {
        // renderComponents makes props available
        // const props = { comments: ['New Comment', 'New Other Comment'] };
        // component = renderComponent(CommentList, null, props);
        const initialState = { comments: ['New Comment', 'New Other Comment'] }; // better call it initialState, not props
        component = renderComponent(CommentList, null, initialState);
    });

    it('shows an LI for each comment', () => {
        expect(component.find('li').length).to.equal(2);

    });

    it('shows each comment that is provided', () => {
        expect(component).to.contain('New Comment');
        expect(component).to.contain('New Other Comment');
    });

});

import { expect } from '../test_helper';
import commentReducer from '../../src/reducers/comments';
import { SAVE_COMMENT } from '../../src/actions/types';

describe('Comments Reducer', () => {

    // in case there is a weird input, we react with the default state
    it('handles action with unknown type', () => {

        // expect(commentReducer()).to.be.instanceof(Array);
        // better:
        // eql compares deeply
        // so we are sure the array is really empty
        // expect(commentReducer()).to.eql([]);
        // failed after we completed the reducer: TypeError: Cannot read property 'type' of undefined
        // so we have to pass in values here:
        expect(commentReducer(undefined, {})).to.eql([]);
    });

    // it('handles action of type SAVE_COMMENT', () => {
    //
    // });
    // better:
    it('SAVE_COMMENT', () => {

        const action = { type: SAVE_COMMENT, payload: 'new comment'};
        expect(commentReducer([], action)).to.eql(['new comment']);

    });

});
